mapper.insert:
INSERT INTO mail (
	sender
	,send_status

	 	,idx


	 	,title


	 	,content

	)
VALUES (
	#{sender}
	,#{send_status}

		,#{idx}


		,#{title}


		,#{content}

)
ON DUPLICATE KEY UPDATE
	send_status = VALUES(send_status),
	title = VALUES(title),
	content = VALUES(content)

mapper.select:
SELECT COUNT(*) FROM file
	WHERE code_name = 'ma001' 
	AND pk_idx = #{mailIdx}
	AND ori_filename = #{ori_filename}

mapper.delete:
DELETE 
	FROM mail_bookmark
WHERE 
	idx = #{idx} 
	AND 
	username = #{username}

mapper.select:
SELECT ori_filename, new_filename, type FROM file 
	WHERE code_name = 'ma001' 
	AND pk_idx = #{idx}

SELECT m.idx ,m.title ,m.is_bookmark ,m.send_date ,(SELECT MIN(idx) FROM file WHERE pk_idx = m.idx AND code_name = 'ma001' ) AS fileYn ,(SELECT MIN(username) FROM mail_receive mr WHERE m.idx = mr.idx GROUP BY idx ) AS username , u.name ,COUNT(*) OVER() AS total_count FROM mail m JOIN user u ON m.username = u.username WHERE sender = 'smpark' AND m.send_status = 'sd'
AND d.subject LIKE CONCAT('%',#{search},'%')
ORDER BY m.idx DESC LIMIT #{limit} OFFSET #{offset}
