mapper.insert:
INSERT INTO mail (
	sender
	,send_status

	 	,idx


	 	,title


	 	,content

	)
VALUES (
	#{sender}
	,#{send_status}

		,#{idx}


		,#{title}


		,#{content}

)
ON DUPLICATE KEY UPDATE
	send_status = VALUES(send_status),
	title = VALUES(title),
	content = VALUES(content)

mapper.select:
SELECT COUNT(*) FROM file
	WHERE code_name = 'ma001' 
	AND pk_idx = #{mailIdx}
	AND ori_filename = #{ori_filename}

mapper.delete:
DELETE 
	FROM mail_bookmark
WHERE 
	idx = #{idx} 
	AND 
	username = #{username}

mapper.select:
SELECT COUNT(idx) 
FROM mail
WHERE idx = #{idx}
AND sender = #{username}

SELECT COUNT(idx) 
FROM mail_receive
WHERE idx = #{idx}
AND username = #{username}

mapper.update:
UPDATE mail
SET
	is_bookmark = #{is_bookmark}
WHERE
	idx = #{idx}
	AND 
	sender = #{username}

UPDATE mail_receive
SET
	is_bookmark = #{is_bookmark}
WHERE
	idx = #{idx}
	AND 
	username = #{username}

mapper.select:
SELECT ori_filename, new_filename, type FROM file 
	WHERE code_name = 'ma001' 
	AND pk_idx = #{idx}

SELECT m.idx, m.title, m.is_bookmark, m.send_date, (SELECT MIN(idx) FROM file WHERE pk_idx = m.idx AND code_name = 'ma001' ) AS fileYn, mr.username, u.name, COUNT(*) OVER() AS total_count FROM mail m JOIN ( SELECT idx, MIN(username) AS username FROM mail_receive GROUP BY idx ) AS mr ON m.idx = mr.idx JOIN user u ON mr.username = u.username WHERE m.sender = #{loginId} AND m.send_status = 'sd' AND m.use_yn = 'Y'
AND m.title LIKE CONCAT('%',#{search},'%')
ORDER BY m.idx DESC LIMIT #{limit} OFFSET #{offset}

SELECT m.idx ,m.title ,m.is_bookmark ,m.send_date ,(SELECT MIN(idx) FROM file WHERE pk_idx = m.idx AND code_name = 'ma001' ) AS fileYn ,(SELECT MIN(username) FROM mail_receive mr WHERE m.idx = mr.idx GROUP BY idx ) AS username ,u.name ,COUNT(*) OVER() AS total_count FROM mail m JOIN user u ON m.sender = u.username WHERE m.sender = #{loginId} AND send_status = 'sv' AND m.use_yn = 'Y'
AND m.title LIKE CONCAT('%',#{search},'%')
ORDER BY m.idx DESC LIMIT #{limit} OFFSET #{offset}

SELECT m.idx ,m.title ,m.send_date ,CASE WHEN m.sender = #{loginId} THEN 'sent' ELSE 'received' END AS mail_type ,COALESCE(mr.username, m.sender) AS username ,u.name ,(SELECT MIN(idx) FROM file WHERE pk_idx = m.idx AND code_name = 'ma001' ) AS fileYn ,COUNT(*) OVER() AS total_count FROM mail m LEFT JOIN ( SELECT mr_inner.idx ,MIN(mr_inner.username ) AS username FROM mail_receive mr_inner GROUP BY mr_inner.idx ) mr ON m.idx = mr.idx LEFT JOIN user u ON u.username = COALESCE(mr.username, m.sender) LEFT JOIN mail_receive mr2 ON m.idx = mr2.idx AND mr2.username = #{loginId} WHERE ( (m.sender = #{loginId} AND m.is_bookmark = 'Y') OR (mr2.username = #{loginId} AND mr2.is_bookmark = 'Y') ) AND (mr2.use_yn = 'Y' or m.use_yn = 'Y')
AND m.title LIKE CONCAT('%',#{search},'%')
ORDER BY m.idx DESC LIMIT #{limit} OFFSET #{offset}

SELECT m.idx ,m.title ,m.send_date ,CASE WHEN m.sender = #{loginId} THEN m.is_bookmark ELSE mr.is_bookmark END AS is_bookmark ,CASE WHEN m.sender = #{loginId} THEN 'sent' ELSE 'received' END AS mail_type ,CASE WHEN m.sender = #{loginId} THEN ( SELECT GROUP_CONCAT(mr_inner.username ORDER BY mr_inner.username ASC LIMIT 1 ) FROM mail_receive mr_inner WHERE mr_inner.idx = m.idx ) ELSE m.sender END AS username ,(SELECT MIN(idx) FROM file WHERE pk_idx = m.idx AND code_name = 'ma001' ) AS fileYn ,u.name ,COUNT(*) OVER() AS total_count FROM mail m LEFT JOIN mail_receive mr ON m.idx = mr.idx AND mr.username = #{loginId} JOIN user u ON m.sender = u.username WHERE (m.sender = #{loginId} OR mr.username = #{loginId}) AND (m.use_from_date IS NOT NULL OR mr.use_from_date IS NOT NULL) AND (m.use_yn = 'Y' OR mr.use_yn = 'Y')
AND m.title LIKE CONCAT('%',#{search},'%')
ORDER BY m.idx DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE mail_receive SET is_read = 'Y' ,read_date = NOW() WHERE idx IN (
#{idx}
) AND username = #{loginId}

mapper.insert:
INSERT INTO 
	file (code_name, ori_filename, new_filename, type, pk_idx)
	SELECT code_name, ori_filename, new_filename, type, #{mailIdx}
	FROM file 
	WHERE new_filename = #{fileId} 
	AND pk_idx = ${originalIdx}
	AND code_name = 'ma001'
