mapper.insert:
INSERT INTO mail (
	sender
	,send_status

	 	,idx


	 	,title


	 	,content

	)
VALUES (
	#{sender}
	,#{send_status}

		,#{idx}


		,#{title}


		,#{content}

)
ON DUPLICATE KEY UPDATE
	send_status = VALUES(send_status),
	title = VALUES(title),
	content = VALUES(content)

mapper.select:
SELECT COUNT(*) FROM file
	WHERE code_name = 'ma001' 
	AND pk_idx = #{mailIdx}
	AND ori_filename = #{ori_filename}
