mapper.select:
SELECT mt.meal_idx, mt.username, mt.is_buy, mt.count, mt.create_date, m.name AS meal_name, CASE WHEN mt.is_buy = 'U' THEN NULL ELSE m.cost END AS total_cost FROM meal_ticket mt LEFT JOIN meal m ON mt.meal_idx = m.meal_idx
mt.username = #{username}
AND m.name LIKE CONCAT('%', #{keyword}, '%')
AND DATE_FORMAT(mt.create_date, '%Y-%m-%d') LIKE CONCAT('%', #{keyword}, '%')
ORDER BY mt.create_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT t.schedule_idx, t.start_date, t.end_date, t.subject, t.create_date, t.username, u.name, (SELECT COUNT(*) FROM trip t LEFT JOIN `user` u ON t.username = u.username WHERE u.username = #{username}
AND t.subject LIKE CONCAT('%', #{keyword}, '%')
) AS totalpage FROM trip t LEFT JOIN `user` u ON t.username = u.username WHERE u.username = #{username} AND t.use_yn = 'Y'
AND t.subject LIKE CONCAT('%', #{keyword}, '%')
ORDER BY t.schedule_idx DESC LIMIT #{offset}, #{limit}

SELECT t.start_date, t.end_date, t.subject, t.content, t.address, u.name, t.schedule_idx
    FROM trip t LEFT JOIN user u ON t.username = u.username
WHERE t.schedule_idx = #{schedule_idx}
