mapper.select:
SELECT
DISTINCT(IFNULL (
(SELECT username FROM user
WHERE name = #{name} AND email = #{email}),
'notFound'
)) AS username from user;

SELECT COALESCE(
(SELECT idx FROM authority_code WHERE username = #{username} AND use_yn =
'Y'),
0
) AS idx;

SELECT
DISTINCT(IFNULL (
(SELECT username FROM user
WHERE name = #{name} AND email = #{email} AND username = #{username}),
'notFound'
)) AS username from user;

SELECT
username, type, subject, content, start_date, end_date FROM career
WHERE username = #{username}

mapper.update:
UPDATE user SET
name = #{name},
email = #{email},
phone = #{phone},
birth = #{birth}
,company_num = #{company_num}
,leave_date = #{leave_date}
WHERE username = #{username}

mapper.select:
SELECT u.name, u.join_date, u.user_yn, u.company_num, u.person_num, u.username, u.title, d.text AS dept_name, c.content AS position_content, CEIL( (SELECT COUNT(*) FROM user u LEFT JOIN department d ON u.parent = d.id LEFT JOIN code c ON u.position = c.name WHERE 1 = 1
AND u.user_yn = #{userYn}
AND (
u.name LIKE CONCAT('%', #{keyword}, '%')
d.text LIKE CONCAT('%', #{keyword}, '%')
c.content LIKE CONCAT('%', #{keyword}, '%')
)
) / #{limit} ) AS totalpage FROM user u LEFT JOIN department d ON u.parent = d.id LEFT JOIN code c ON u.position = c.name WHERE 1 = 1
AND u.user_yn = #{userYn}
AND (
u.name LIKE CONCAT('%', #{keyword}, '%')
d.text LIKE CONCAT('%', #{keyword}, '%')
c.content LIKE CONCAT('%', #{keyword}, '%')
)
ORDER BY u.join_date DESC LIMIT #{offset}, #{limit}

mapper.update:
UPDATE department SET text = #{text}, use_yn =
#{use_yn}, use_date = #{use_date}, content = #{content} WHERE id =
#{id}

mapper.select:
SELECT u.name AS name, u.person_num AS person_num, u.username AS username, ds.parent AS old_department, ds.id AS old_position, NULL AS title, NULL AS dept_name, NULL AS owner, NULL AS parent, NULL AS text FROM user u LEFT JOIN direct_store ds ON u.parent = ds.id WHERE u.username = #{username}
UNION ALL
SELECT NULL AS name, NULL AS person_num, NULL AS username, NULL AS old_department, NULL AS old_position, ds.id AS title, ds.name AS dept_name, ds.owner AS owner, ds.parent AS parent, d.text AS text FROM direct_store ds LEFT JOIN department d ON ds.parent = d.id WHERE ds.parent IN ( SELECT parent FROM direct_store WHERE owner = #{username} )
